var logFunctionCalls = function (f, name) {
	var stringify = function (x) {
		if (typeof x == "function") {
			return '(function ' + x.name + ')';
		} else {
			try {
				return JSON.stringify(x);
			} catch (e) {
				return x.toString();
			}
		}
	};

	return function () {
		var call = '';
		// context
		if (this && this != window) {
			call += stringify(this);
		}

		// name
		call += name || f.name || 'anonymous';

		// arguments
		var first = true;
		call += '(';
		for (var i = 0; i < arguments.length; i++) {
			if (first) {
				first = false;
			} else {
				call += ', ';
			}

			call += stringify(arguments[i]);
		}
		call += ')';

		// Call the function, log the call and result
		console.log('Calling ' + call);
		var result = f.apply(this, arguments);
		console.log(call + ' = ' + stringify(result));
		return result;
	};
};

var googleMap =(function(){
    var map;
    return {
        init_map: function ( id , center) {
            var myOptions = {
                zoom: 11,
                center: center,
                mapTypeId: google.maps.MapTypeId.ROADMAP,
                panControl: false,
                zoomControl: false,
                scaleControl: false,
                mapTypeControl: false,
                streetViewControl: false
            };
            map = new google.maps.Map(document.getElementById( id ), myOptions);
            return map;
        },
        addPlaceMark:function (point,tooltip) {
            var marker = new google.maps.Marker({
                position: new google.maps.LatLng(point.lat,point.lng),
                title:tooltip
            });
            marker.setMap(map);
            return marker;

            var marker = new google.maps.Marker({
                map: map,
                draggable:false,
                position: new google.maps.LatLng(point.lat,point.lng),
                visible: true
            });
        }
    };
})();

var getUserPosition = function(user_position, cbk){
    if(navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(function(position){
            console.log(position);
            user_position.lat = position.coords.latitude;
            user_position.lng = position.coords.longitude;
        });
    }
    cbk(user_position);
}

function validateEmail(email) {
    var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(email);
}

function validateName(name) {
    var re = /^\S{2,}[ ]+\S{2,}$/;
    var b=re.test(name)
    return b;
}
